import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const TimesheetForm = ({ selectedDate, onClose }) => {
  const [formData, setFormData] = useState({
    date: selectedDate ? `${selectedDate.month} ${selectedDate.day}, ${selectedDate.year}` : '',
    checkIn: '', // Changed to be a timestamp for easier time difference calculations
    checkOut: '',
  });

  const [isCheckIn, setIsCheckIn] = useState(true);

  useEffect(() => {
    // Fetch check-in time when the component mounts
    getCheckInTime();
  }, []);

  const getCheckInTime = async () => {
    try {
      const authToken = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/timesheet', {
        method: 'GET',
        headers: {
          Authorization: authToken,
        },
      });
      if (response.ok) {
        const data = await response.json();
        if (data.startDate != null) {
          // Convert the start date to a timestamp for better handling
          setFormData((prevFormData) => ({
            ...prevFormData,
            checkIn: new Date(data.startDate).toISOString(),
          }));
        }
      }
    } catch (error) {
      console.error('Error getting start check-in data:', error);
    }
  };

  const setCheckInTime = async () => {
    try {
      const authToken = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/timesheet', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: authToken,
        },
        body: JSON.stringify({
          startDate: new Date().toISOString(), // Include the check-in date in the request
        }),
      });

      // Handle the response
      if (response.ok) {
        console.log('Check-in date saved successfully.');
        // You can perform additional actions here if needed
      } else {
        console.error('Error saving check-in date:', response.statusText);
      }
    } catch (error) {
      console.error('Error saving check-in date:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleToggleCheckInOut = () => {
    // Toggle between check-in and check-out modes
    setIsCheckIn((prevIsCheckIn) => !prevIsCheckIn);

    // If checking out, update checkOut time
    if (!isCheckIn) {
      setFormData((prevFormData) => ({
        ...prevFormData,
        checkOut: new Date().toISOString(),
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Add logic to handle form submission, e.g., send data to the server or update state
    console.log(formData);

    // Calculate and log the time difference if checking out
    if (!isCheckIn) {
      const checkInTime = new Date(formData.checkIn);
      const checkOutTime = new Date(formData.checkOut);
      const timeDifference = checkOutTime - checkInTime;
      console.log(`Time difference: ${timeDifference} milliseconds`);

      // Save the check-in date
      await setCheckInTime();
      // Add logic to save the time difference to the server or update state
    }

    onClose(); // Close the form after submission
  };

  return (
    <div className="dropdown-menu d-block position-static p-2 mx-0 shadow rounded-3 w-340px" data-bs-theme="light">
      <div className="overlay">
        <form onSubmit={handleSubmit}>
          {/* ... (unchanged code) */}
          <div className="mb-3">
            <label htmlFor="checkIn" className="form-label">
              {isCheckIn ? 'Check-in' : 'Check-out'}:
            </label>
            <input
              type="time"
              name={isCheckIn ? 'checkIn' : 'checkOut'}
              value={isCheckIn ? formData.checkIn.slice(11, 16) : formData.checkOut.slice(11, 16)}
              onChange={handleInputChange}
              className="form-control"
              required
            />
          </div>
          {/* ... (unchanged code) */}
        </form>
      </div>
    </div>
  );
};

export default TimesheetForm;
