import React, { useState } from 'react';

const TimesheetForm = () => {
  const [formData, setFormData] = useState({
    date: '',
    checkIn: '',
    checkOut: '',
    timeWorked: '',
  });

  const [isCheckIn, setIsCheckIn] = useState(true);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleToggleCheckInOut = () => {
    setIsCheckIn(!isCheckIn);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic to handle form submission, e.g., send data to the server or update state
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Date:
        <input type="date" name="date" value={formData.date} onChange={handleInputChange} required />
      </label>
      <br />

      <label>
        {isCheckIn ? 'Check-in' : 'Check-out'}:
        <input
          type="time"
          name={isCheckIn ? 'checkIn' : 'checkOut'}
          value={isCheckIn ? formData.checkIn : formData.checkOut}
          onChange={handleInputChange}
          required
        />
      </label>
      <br />

      <label>
        Amount time worked:
        <input
          type="text"
          name="timeWorked"
          value={formData.timeWorked}
          onChange={handleInputChange}
          required
        />
      </label>
      <br />

      <button type="button" onClick={handleToggleCheckInOut} style={{ backgroundColor: isCheckIn ? 'blue' : 'red' }}>
        {isCheckIn ? 'Check-in' : 'Check-out'}
      </button>

      <button type="submit">Submit</button>
    </form>
  );
};

export default TimesheetForm;
