import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";

const ViewGroup = () => {
  const [isPartOfGroup, setIsPartOfGroup] = useState(false);
  const [leaveMessage, setLeaveMessage] = useState("");
  const [groupUsers, setGroupUsers] = useState([]);

  useEffect(() => {
    // Make an API call to retrieve group membership info from your backend
    const fetchGroupMembership = async () => {
      try {
        const response = await fetch('/api/group/membership', {
          method: 'GET',
          headers: {
            // Add any necessary headers like authentication tokens here
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          setIsPartOfGroup(data.isPartOfGroup);
          setGroupUsers(data.groupUsers);
        } else {
          throw new Error('Failed to fetch group membership information');
        }
      } catch (error) {
        console.error('Error fetching group membership:', error);
      }
    };

    fetchGroupMembership();
  }, []); // Run this effect only once on component mount

  const handleLeaveGroup = () => {
    // Perform actions to leave the group
    // For demonstration, updating state to simulate leaving the group
    localStorage.setItem('joinedGroup', 'false');
    setIsPartOfGroup(false);
    setLeaveMessage("You have left the group.");
  };

  return (
    <div className="container">
      <h1>Group View</h1>
      {isPartOfGroup ? (
        <div>
          <h2>Users in the Group:</h2>
          <ul>
            {groupUsers.map((user) => (
              <li key={user.id}>{user.name}</li>
            ))}
          </ul>
          <button className="btn btn-danger" onClick={handleLeaveGroup}>
            Leave Group
          </button>
          {leaveMessage && <p>{leaveMessage}</p>}
        </div>
      ) : (
        <p>You are not currently part of the group.</p>
      )}
    </div>
  );
};

export default ViewGroup;
