import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import RightArrow from "./arrowR.png";

const TimesheetForm = ({ selectedDate }) => {
  const [formData, setFormData] = useState({
    month: '',
    year: '',
    checkIn: '',
    checkOut: '',
    timeWorked: '',
  });

  useEffect(() => {
    // Update formData when selectedDate changes
    if (selectedDate instanceof Date && !isNaN(selectedDate)) {
      setFormData({
        month: selectedDate.toLocaleDateString('en-US', { month: 'long' }),
        year: selectedDate.getFullYear(),
        checkIn: '',
        checkOut: '',
        timeWorked: '',
      });
    }
  }, [selectedDate]);

  const [isCheckIn, setIsCheckIn] = useState(true);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleToggleCheckInOut = () => {
    setIsCheckIn(!isCheckIn);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic to handle form submission, e.g., send data to the server or update state
    console.log(formData);
  };

  return (
    <div className="dropdown-menu d-block position-static p-2 mx-0 shadow rounded-3 w-340px" data-bs-theme="light">
      <div className="overlay">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="date" className="form-label">
              Date:
            </label>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleInputChange}
              className="form-control"
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="checkIn" className="form-label">
              {isCheckIn ? 'Check-in' : 'Check-out'}:
            </label>
            <input
              type="time"
              name={isCheckIn ? 'checkIn' : 'checkOut'}
              value={isCheckIn ? formData.checkIn : formData.checkOut}
              onChange={handleInputChange}
              className="form-control"
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="timeWorked" className="form-label">
              Amount time worked:
            </label>
            <input
              type="text"
              name="timeWorked"
              value={formData.timeWorked}
              onChange={handleInputChange}
              className="form-control"
              required
            />
          </div>

          <div className="col text-center">
            <div className="row">
              <div className="col">
                <button
                  type="button"
                  onClick={handleToggleCheckInOut}
                  className={`btn ${isCheckIn ? 'btn-primary' : 'btn-danger'} mb-2`}
                >
                  {isCheckIn ? 'Check-in' : 'Check-out'}
                </button>
              </div>
            </div>

            <div className="row">
              <div className="col">
                <a href="#" className="btn btn-link text-dark mb-2">
                  Submit
                </a>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TimesheetForm;
