import React, { useState } from "react";

const NameList = () => {
  const initialNames = ["John", "Jane", "Bob", "Alice"];
  const [names, setNames] = useState(initialNames);
  const [showModal, setShowModal] = useState(false);
  const [selectedNameIndex, setSelectedNameIndex] = useState(null);

  const handleDelete = (index) => {
    // Set the selected index and show the modal
    setSelectedNameIndex(index);
    setShowModal(true);
  };

  const handleConfirmDelete = () => {
    // Create a copy of the names array
    const updatedNames = [...names];
    // Remove the name at the specified index
    updatedNames.splice(selectedNameIndex, 1);
    // Update the state with the new array and hide the modal
    setNames(updatedNames);
    setShowModal(false);
  };

  const handleCancelDelete = () => {
    // Reset the selected index and hide the modal
    setSelectedNameIndex(null);
    setShowModal(false);
  };

  return (
    <div className="container">
      <h2 style={{ marginTop: '30px' }}>Name List</h2>
      <ul className="list-group" style={{ marginBottom: '30px' }}>
        {names.map((name, index) => (
          <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
            {name}
            <button className="btn btn-danger" onClick={() => handleDelete(index)}>
              Remove
            </button>
          </li>
        ))}
      </ul>

      {/* Confirmation Modal */}
      {showModal && (
        <div className="modal">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Are you sure?</h5>
                <button type="button" className="close" onClick={handleCancelDelete}>
                  <span>&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <p>Do you really want to remove this name?</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleCancelDelete}>
                  No
                </button>
                <button type="button" className="btn btn-danger" onClick={handleConfirmDelete}>
                  Yes
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NameList;
