import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./weekday.css";

const Calendar = () => {
    const daysOfWeek = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

    const months = [
        "January", "February", "March", "April",
        "May", "June", "July", "August",
        "September", "October", "November", "December"
    ];

    const [activeMonth, setActiveMonth] = useState("June"); // Initial active month

    const handleMonthChange = (event) => {
        setActiveMonth(event.target.value);
    };

    const getDaysInMonth = (month, year) => {
        const firstDay = new Date(year, months.indexOf(month), 1).getDay();
        const lastDay = new Date(year, months.indexOf(month) + 1, 0).getDate();
        
        const days = Array.from({ length: lastDay }, (_, i) => i + 1);
        const leadingEmptyDays = Array.from({ length: firstDay }, (_, i) => '');
    
        return [...leadingEmptyDays, ...days];
    };

    const daysInMonth = getDaysInMonth(activeMonth, new Date().getFullYear());

    return (
        <div class="dropdown-menu d-block position-static p-2 mx-0 shadow rounded-3 w-340px" data-bs-theme="light">
        <div class="d-grid gap-1">
          <div class="cal">
            <div class="cal-month">
            <button className="btn" type="button">
              <svg className="bi" width="16" height="16"><use href="#arrow-left-short"></use></svg>
            </button>
            <strong className="cal-month-name">{`${activeMonth} ${activeYear}`}</strong>
            {/* Month selection dropdown */}
            <select className="form-select cal-month-name" value={activeMonth} onChange={handleMonthChange}>
              {months.map((month) => (
                <option key={month} value={month}>{month}</option>
              ))}
            </select>
            {/* Year selection input */}
            <input
              type="number"
              className="form-control cal-year-input"
              value={activeYear}
              onChange={handleYearChange}
            />
            <button className="btn" type="button">
              <svg className="bi" width="16" height="16"><use href="#arrow-right-short"></use></svg>
            </button>
            </div>

            <div className="cal-weekdays text-body-secondary">
                {daysOfWeek.map((weekday) => (
                <div key={weekday} className="cal-weekday">{weekday}</div>
                ))}
          </div>

            <div className="cal-days">
                {daysInMonth.map((day) => (
                <button key={day} className={`btn cal-btn ${day > 0 ? '' : 'disabled'}`} type="button" disabled={day <= 0}>
                    {day > 0 ? day : ''}
                </button>
                ))}
            </div>
          </div>
        </div>
      </div>
    );
};
export default Calendar;
