import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.css";

const ViewGroup = () => {
  const [isPartOfGroup, setIsPartOfGroup] = useState(true); // Set initial state to true if the user is part of the group

  const handleLeaveGroup = () => {
    // Perform actions to leave the group (e.g., API calls, state updates)
    // For demonstration purposes, simply updating state to indicate the user has left the group
    setIsPartOfGroup(false);
  };

  return (
    <div className="container">
      <h1>Group View</h1>
      {isPartOfGroup ? (
        <button className="btn btn-danger" onClick={handleLeaveGroup}>
          Leave Group
        </button>
      ) : (
        <p>You have left the group.</p>
      )}
    </div>
  );
};

export default ViewGroup;
