import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const TimesheetForm = () => {
  const [formData, setFormData] = useState({
    date: '',
    checkIn: '',
    checkOut: '',
    timeWorked: '',
  });

  const [isCheckIn, setIsCheckIn] = useState(true);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleToggleCheckInOut = () => {
    setIsCheckIn(!isCheckIn);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic to handle form submission, e.g., send data to the server or update state
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="container mt-5">
      <div className="mb-3">
        <label htmlFor="date" className="form-label">
          Date:
        </label>
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>

      <div className="mb-3">
        <label htmlFor="checkIn" className="form-label">
          {isCheckIn ? 'Check-in' : 'Check-out'}:
        </label>
        <input
          type="time"
          name={isCheckIn ? 'checkIn' : 'checkOut'}
          value={isCheckIn ? formData.checkIn : formData.checkOut}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>

      <div className="mb-3">
        <label htmlFor="timeWorked" className="form-label">
          Amount time worked:
        </label>
        <input
          type="text"
          name="timeWorked"
          value={formData.timeWorked}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>

      <button
        type="button"
        onClick={handleToggleCheckInOut}
        className={`btn ${isCheckIn ? 'btn-primary' : 'btn-danger'}`}
      >
        {isCheckIn ? 'Check-in' : 'Check-out'}
      </button>

      <button type="submit" className="btn btn-success mt-3">
        Submit
      </button>
    </form>
  );
};

export default TimesheetForm;
