import React, { useState, useRef } from 'react';

const Timer = () => {
  const [elapsedTime, setElapsedTime] = useState(0);
  const [timerRunning, setTimerRunning] = useState(false);
  const intervalRef = useRef(null);


  const saveTimerData = (timerData) => {
    fetch('http://localhost:3000/timer', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(timerData),
    })
      .then(response => response.json())
      .then(data => setElapsedTime(data))
      .catch(error => console.error('Error saving timer data:', error));
  };

  const startTimer = () => {
    if (!timerRunning) {
      const startTime = Date.now() - elapsedTime;
      setTimerRunning(true);

      intervalRef.current = setInterval(() => {
        const currentTime = Date.now();
        const newElapsedTime = currentTime - startTime;
        setElapsedTime(newElapsedTime);
      }, 1000);
    }
  };

  const stopTimer = () => {
    setTimerRunning(false);
    clearInterval(intervalRef.current);
  };

  const displayTime = (milliseconds) => {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <div>
      <button onClick={startTimer}>Start Timer</button>
      <button onClick={stopTimer}>Stop Timer</button>
      <p id="timerDisplay">Timer: {displayTime(elapsedTime)}</p>
    </div>
  );
};

export default Timer;
