import React, { useState, useEffect } from "react";

const NameList = () => {
  const [initialNames, setInitialNames] = useState([]);
  const [names, setNames] = useState([]);
  const [user, setUser] = useState(null);
  useEffect(() => {

    const userData = async () =>{
      try{
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/getUser', {
          method: 'GET',
          headers: {
            Authorization: token,
          },
        });
        if(response.ok)
        {
          const data = await response.json();
          setUser(data);
        }
      }
      catch (error) {
        console.error('Error fetching group data:', error.message);
      }
    }

    const getNames = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/listName', {
          method: 'GET',
          headers: {
            Authorization: token,
          },
        });

        if (response.ok) {
          const groupData = await response.json();
          setInitialNames(groupData.members);
          const nameArr = [];
          groupData.members.forEach(memName => {
            
            nameArr.push(memName.name);
          });
          console.log(nameArr);
          setNames(groupData.members); // Set both initial and current names
        } else {
          console.error('Error fetching group data:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching group data:', error.message);
      }
    };

    getNames();
  }, []); // Empty dependency array to run the effect only once on mount

  const handleDelete = (index) => {
    // Create a copy of the names array
    const updatedNames = [...names];
    // Remove the name at the specified index
    updatedNames.splice(index, 1);
    // Update the state with the new array
    setNames(updatedNames);
  };

  return (
    <div className="container">
      <h2 style={{ marginTop: '30px' }}>Name List</h2>
      <ul className="list-group" style={{ marginBottom: '30px' }}>
        {names.map((name, index) => (
          <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
            {name}
            <button className="btn btn-danger" onClick={() => handleDelete(index)}>
              Remove
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default NameList;
