import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.css";
import axios from "axios";
//import React, { useState } from "react";
import { useNavigate } from "react-router";
 
export default function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [emailAddress, setEmailAddress] = useState('');
  //alert("helo");

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      console.log("Handling registration:", { username, emailAddress, password });
  
      const response = await axios.post('/register', { username, emailAddress, password });
  
      console.log('Server response:', response.data);
  
      alert(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Registration failed:', error.message);
    }
  };
  
  const mainStyle = {
      // Define your styles here
      // For example:
      backgroundColor: "#f0f0f0",
      padding: "20px",
      maxWidth: "400px"
  };

  const formStyle = {
      // Define your styles here
      // For example:
      maxWidth: "400px",
  };

  return (
  <main style={mainStyle} className="form-signin d-flex align-items-center">
      <form className="w-100 max-width-form" onSubmit={handleRegister}>
        <h1 className="h3 mb-3 fw-normal">Register</h1>
        <div className="form-floating">
          <input
            type="text"
            className="form-control"
            id="username"
            placeholder="Your Full Name"
            value={username}
            onChange={(e)=>setUsername(e.target.value)}
          />
          <label htmlFor="userName">Full Name</label>
        </div>
        <div className="form-floating">
          <input
            type="email"
            className="form-control"
            id="emailAddress"
            placeholder="name@example.com"
            value={emailAddress}
            onChange={(e)=>setEmailAddress(e.target.value)}
          />
          <label htmlFor="email">Email address</label>
        </div>
        <div className="form-floating mb-3">
          <input
            type="password"
            className="form-control"
            id="password"
            placeholder="Password"
            value={password}
            onChange={(e)=>setPassword(e.target.value)}
          />
          <label htmlFor="password">Password</label>
        </div>

        <button className="btn btn-primary w-100 py-2" type="submit">
          Sign in
        </button>
        <p className="mt-5 mb-3 text-body-secondary">© 2017–2023</p>
      </form>
  </main>
  );
};
