import React from "react";
import "bootstrap/dist/css/bootstrap.css";
import RightArrow from "../Timesheet/calendarAssets/arrowR.png";

const CreateGroup = () => {
    const token = localStorage.getItem('token');
    const isLoggedIn = !!token;

    return (
        <div className={`container${isLoggedIn ? '' : ' disabled'}`}>
            {!isLoggedIn && (
                <div className="overlay">
                    <p className="overlay-message">Must be a user to create a group</p>
                </div>
            )}

            <div className="row">
                <h4>Enter a Group Name:</h4>
            </div>
            <div className="row">
                <form className="d-flex" role="search">
                    <input
                        className="form-control me-2"
                        type="search"
                        placeholder="Name"
                        aria-label="Search"
                        disabled={!isLoggedIn}
                    />
                    <button className="btn" style={{ marginBottom: '5px' }} disabled={!isLoggedIn}>
                        <img src={RightArrow} className="bi" width="16" height="18" />
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CreateGroup;
